const parent_cards=document.getElementById("cards"),all_projects=[{picture:"./picture/amh.webp",desc:"Allo Maître d’Ho",date:"11 June 2022"},{picture:"https://google.com",desc:"unicityCoin",date:"11 June 2022"},{picture:"https://google.com",desc:"test",date:"11 June 2022"},{picture:"https://google.com",desc:"test",date:"11 June 2022"}],projet='\n  <div id="picture">\n  </div>\n  <div id="card">\n  </div>\n';document.querySelector("#cards").innerHTML+=projet;const parent_picture=document.querySelector("#picture"),parent_card=document.querySelector("#card");let id=0;function card_template(e,t,n){const c=`\n    <img id="${id}" class="__front img" src="${e}">\n  `,r=`\n    <div class="__back">\n      <div class="__text">    \n        <p>${t}</p>\n        <p>Create at June 11.07.2022</p>\n      </div>\n    </div>\n  `,d=id;return id++,{img:c,card:r,i:d}}function load(){let e=0;document.querySelectorAll(".__front.img").forEach((t=>{t.addEventListener("mouseenter",(function(e){e.target.classList.add("__full")})),t.addEventListener("mouseleave",(function(e){e.target.classList.remove("__full")})),t.style.marginTop+=50*e+"px",e++}))}addEventListener("load",(()=>{console.log("load");for(let e=0;e<all_projects.length;e++){const t=all_projects[e],n=card_template(t.picture,t.desc,t.date);n.i,parent_picture.innerHTML+=n.img,parent_card.innerHTML+=n.card}load()}));